import React, { useState } from 'react';
import M from 'materialize-css';
import 'materialize-css/dist/css/materialize.min.css';
import { useMutation } from '@apollo/client';
import { SAVE_TRIP } from '../utils/mutations';
// import Auth from '../utils/auth';

const TripForm = () => {

const [userFormData, setUserFormData] = useState({ 
    "title": '', 
    "location": '',
    "start": '',
    "end": '',
    "description": ''
});

const [saveTrip, { error }] = useMutation(SAVE_TRIP);

const handleInputChange = (event) => {
    console.log(event.target);
}

const handleFormSubmit = async (event) => {
        event.preventDefault();



        return (
            <div>
                <button class="waves-effect waves-light btn modal-trigger" data-target="modal1">Modal</button>

                <div id="modal1" class="modal">
                    <div class="modal-content">
                        <h4>Make a new trip:</h4>

                        <div class="row">
                            <form class="col s12">
                                <div class="row modal-form-row">
                                    <div class="input-field col s12">
                                        <input id="title" type="text" class="validate" name="title" onChange={handleInputChange} value={userFormData.title} required/>
                                        <label for="title">Trip Name</label>
                                    </div>
                                </div>
                                <div class="row modal-form-row">
                                    <div class="input-field col s12">
                                        <input id="location" type="text" class="validate" name="location" onChange={handleInputChange} value={userFormData.location} required/>
                                        <label for="location">Trip Name</label>
                                    </div>
                                </div>
                                <div class="row">
                                    <div class="input-field col s12">
                                        <input id="start" type="text" class="validate" name="start" onChange={handleInputChange} value={userFormData.start} required/>
                                        <label for="start">Start Date:</label>
                                    </div>
                                </div>
                                <div class="row">
                                    <div class="input-field col s12">
                                        <input id="end" type="text" class="validate" name="end" onChange={handleInputChange} value={userFormData.end} required/>
                                        <label for="end">End Date:</label>
                                    </div>
                                </div>
                                <div class="row">
                                    <div class="input-field col s12">
                                        <textarea id="description" type="text" class="materialize-textarea validate" name="description" onChange={handleInputChange} value={userFormData.description}/>
                                        <label for="description">Description</label>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                    <div class="modal-footer">
                        <a class=" modal-action modal-close waves-effect waves-green btn-flat">Add trip</a>
                    </div>
                </div>
            </div>
        )
    }
};




export default TripForm;
